SELECT * FROM Subject WHERE id = (SELECT subject_id FROM (SELECT subject_id, AVG(mark) AS average_mark FROM Mark GROUP BY subject_id) WHERE average_mark = (SELECT MAX(average_mark) FROM (SELECT subject_id, AVG(mark) AS average_mark FROM Mark GROUP BY subject_id)));
SELECT * FROM Student WHERE id IN (SELECT student_id FROM (SELECT student_id, SUM(amount) AS total_paid FROM Payment GROUP BY student_id) WHERE total_paid < (SELECT AVG(total_paid) FROM (SELECT student_id, SUM(amount) AS total_paid FROM Payment GROUP BY student_id)));